INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2020-02-04 18:34:05', '2020-02-04 17:34:05', '<!-- wp:paragraph -->\n<p>Ongi etorri WordPressera. Hau da zure lehen bidalketa. Editatu edo ezabatu ezazu, eta hasi idazten!</p>\n<!-- /wp:paragraph -->', 'Kaixo mundua!', '', 'publish', 'open', 'open', '', 'kaixo-mundua', '', '', '2020-02-04 18:34:05', '2020-02-04 17:34:05', '', 0, 'http://localhost:5050/?p=1', 0, 'post', '', 1),
(2, 1, '2020-02-04 18:34:05', '2020-02-04 17:34:05', '<!-- wp:paragraph -->\n<p>Hau orrialde baten adibidea da. Blog bidalketa batengatik desberdina da leku bakarrean egongo delako eta zure guneko nabigazioan agertuko delako (itxura gehienetan). Jende gehiena \"Honi buruz\" izeneko orrialde bat sortuz hasten da, guneko bistari potentzialei bere burua aurkezten diona. Honen antzerako zeozer esan beharko luke:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Kaixo denei! Egunez harri-jasotzailea naiz, gauez berriz bertsolaria, eta hau da nire webgunea. Abaltzisketan bizi naiz, Pintxo izeneko txakur handi bat dut eta sagardoa gustoko dut. (Eta sirimiriaz bustitzea ere)</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>...edo horrelako zer edo zer:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>XYZ Tramankulu Enpresa 1971an sortu zen, eta ordudanik kalitatzeko tramankuluak hornitzen ditu publikoarentzat. Gotham Hirian kokatuta, XYZ lana ematen dio 2000 lagun baina gehiagori eta mota guztietako ikaragarrizko gauzak egiten ditu Gothamgo komunitatearentzat.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>WordPress erabiltzaile berria zarenez, <a href=\"http://localhost:5050/wp-admin/\">zure arbelera</a> joan beharko zinateke orrialde hau ezabatu eta zure edukirako orrialde berriak sortzeko. Ondo pasa!</p>\n<!-- /wp:paragraph -->', 'Adibide-orrialdea', '', 'publish', 'closed', 'open', '', 'adibide-orrialdea', '', '', '2020-02-04 18:34:05', '2020-02-04 17:34:05', '', 0, 'http://localhost:5050/?page_id=2', 0, 'page', '', 0),
(3, 1, '2020-02-04 18:34:05', '2020-02-04 17:34:05', '<!-- wp:heading --><h2>Nortzuk gara</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Gure gunearen helbidea: http://localhost:5050</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Zein informazio pertsonal biltzen dugun eta zergatik biltzen dugun</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Iruzkinak</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Bisitariak gunean iruzkinak uzten dituztenean zabor-iruzkinak detektatzeko iruzkin formulategiko datuez gain, bisitariaren IP helbidea eta nabigatzailearen user agent katea gordetzen ditugu. </p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Gravatar zerbitzuari zure e-posta helbidetik sortutako kate anonimo bat (hash izenez ere ezagutua) helarazi ahal zaio hura erabiltzen ari zaren ikusteko. Gravatar zerbitzuko pribatutasun politika hemen duzu eskuragarri: https://automattic.com/privacy/. Zure iruzkinaren onarpenaren ondoren, zure profileko irudia publikoki ikusgai dago zure iruzkinaren testuinguruan.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Media</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Zure gunera irudiak igotzen badituzu, ekidin irudi hauek (EXIF GPS)  posizioen informazioa txertaturik izatea. Zure guneko bisitariek hauek deskargatu eta irudietatik posizioen inguruko informazioa ateratzeko aukera izango dute. </p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Kontaktu inprimakiak</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Cookie-ak</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Gure gunean iruzkin bat uzten baduzu, cookietan zure izena, e-posta helbidea eta webgunea gordetzea hautatu dezakezu. Hau zure erosotasunerako da, beste iruzkin bat uztean zure datuak berriro bete behar ez ditzazun. Cookie hau urtebetez gordeko da.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Izen-emate orrialdea bisitatuz gero, aldi baterako cookiea bidaltzen dugu zure nabigatzaileak cookieak onartzen dituen edo ez jakiteko. Cookie honek ez du inolako datu pertsonalik gordetzen eta zure nabigatzailea ixtean baztertu egiten da. </p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Saioa hasten duzunean, zenbait cookie ere instalatuko ditugu zure saio hasierako informazioa eta zure pantailaren bistaratze aukerak gordetzeko. Saio hasierako cookieak bi egun irauten dute, eta pantailaren aukerek urtebete irauten dute. \"Gogorarazi iezaidazu\" aukeratzen baduzu, zure hasiera saioa bi astez mantenduko da. Zure kontuko saioa bukatzen baduzu, saio hasierako cookieak ezabatu egingo dira. </p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Artikulu bat editatzen edo argitaratzen duzunean, cookie gehigarri bat gordeko da zure nabigatzailean.Cookie honek ez du datu pertsonalik gordetzen, editatutako bidalketaren IDa adierazten da soilik eta egun batera iraungitzen da.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Beste gune batzuetatik txertatutako edukia</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Gune honetako artikuluek kapsulatutako edukia izan dezakete (adibidez: bideoak, irudiak, artikuluak etb.) Beste webgune batzuetako eduki kapsulatuaren portaera, bisitariak webgune hori bisitatzearen parekoa da. </p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Webgune hauek zuri buruzko datuak jaso ditzakete, cookieak erabili, hirugarrenen segimendu gehigarri bat txertatu, eta txertatutako eduki horrekin duzun elkarrekintza monitorizatu, txertatutako edukiarekin duzun elkarrekintzaren segimendua barne webgune horretan kontu bat izan eta saioa hasita baduzu.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Analitikak</h3><!-- /wp:heading --><!-- wp:heading --><h2>Zure informazioa zeinekin partekatzen dugun</h2><!-- /wp:heading --><!-- wp:heading --><h2>Noiz arte gordetzen ditugu zure datuak</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Iruzkin bat uzten baduzu,iruzkina eta honen metadatuak muga gabe gordetzen dira. Honi esker jarraipen iruzkinak automatikoki ezagutu eta onartu ditzakegu moderazio ilera batean mantendu beharrean. </p><!-- /wp:paragraph --><!-- wp:paragraph --><p>Gure webgunean izena ematen duten erabiltzaileek (erabiltzailerik bada) beraien erabiltzaile profilean helarazten duten informazioa gordetzen dugu. Erabiltzaile orok, ikusi, editatu edo ezabatu dezake bere informazio pertsonala edozein unetan (erabiltzaile izena izan ezik, hau ezin baita aldatu). Webguneko administratzaileek ere informazio hau ikusi eta editatu dezakete.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Zeintzuk eskubide dituzun zure datuekiko</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Gune honetan erabiltzaile konturik baduzu edo eta iruzkinak idatzi badituzu, esportatutako fitxategi bat edo eta zure inguruan dugun informazioa eskatu dezakezu, zuk guri helarazitako informazio guztia barne. Honez gain, zure inguruan ditugun datu pertsonal guztiak ezabatzeko eskaera egin dezakezu. Honek ez ditu arrazoi administratiboengatik, legediagatik edo eta segurtasun arrazoiengatik gu gordetzera behartuta gauden datuak barne hartzen.  </p><!-- /wp:paragraph --><!-- wp:heading --><h2>Zure datuak nora bidaltzen ditugun</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Bisitarien iruzkinak automatikoki egiaztatuko dira zabor-iruzkin detekzio zerbitzu batekin.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Zure harremanetarako informazioa</h2><!-- /wp:heading --><!-- wp:heading --><h2>Informazio gehiago</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Zure informazioa nola babesten dugun</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Datu arrakalen aurrean zein prozedura ditugu abian</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Zein hirugarrenengandik jasotzen ditugu datuak</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Zein automatizatutako erabakitze mota edo/eta soslaiatze egiten ditugu erabiltzailearen datuekin</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3> Arloko informazio ezagutarapenaren eskakizun erregulatzaileak</h3><!-- /wp:heading -->', 'Pribatutasun politika', '', 'draft', 'closed', 'open', '', 'Pribatutasun politika', '', '', '2020-02-04 18:34:05', '2020-02-04 17:34:05', '', 0, 'http://localhost:5050/?page_id=3', 0, 'page', '', 0),
(4, 1, '2020-02-04 18:34:12', '0000-00-00 00:00:00', '', 'Zirriborro automatikoa', '', 'auto-draft', 'open', 'open', '', '', '', '', '2020-02-04 18:34:12', '0000-00-00 00:00:00', '', 0, 'http://localhost:5050/?p=4', 0, 'post', '', 0);

